import{j as e,c as a,b as r,ah as n}from"./chunks/framework.Br27zpCu.js";const g=JSON.parse('{"title":"Native-safe Routing Rules","description":"","frontmatter":{},"headers":[],"relativePath":"bagisto-native/integrating-native-framework/native-ready/native-safe-routing-rules.md","filePath":"bagisto-native/integrating-native-framework/native-ready/native-safe-routing-rules.md","lastUpdated":1769095842000}'),i={name:"bagisto-native/integrating-native-framework/native-ready/native-safe-routing-rules.md"};function o(s,t,l,d,c,u){return r(),a("div",null,[...t[0]||(t[0]=[n('<h1 id="native-safe-routing-rules" tabindex="-1">Native-safe Routing Rules <a class="header-anchor" href="#native-safe-routing-rules" aria-label="Permalink to &quot;Native-safe Routing Rules&quot;">​</a></h1><p>Bagisto Native relies on <strong>URL Pattern Matching</strong> to determine if a link should open in the current WebView, a new Modal, or an External Browser.</p><h2 id="standard-rules" tabindex="-1">Standard Rules <a class="header-anchor" href="#standard-rules" aria-label="Permalink to &quot;Standard Rules&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Pattern</th><th style="text-align:left;">Action</th><th style="text-align:left;">Use Case</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Same Domain</strong></td><td style="text-align:left;">Turbo Drive Navigation</td><td style="text-align:left;">Moving from Product -&gt; Category.</td></tr><tr><td style="text-align:left;"><strong>Different Domain</strong></td><td style="text-align:left;">External Browser</td><td style="text-align:left;">Opening Payment Gateways or Social Links.</td></tr></tbody></table><h2 id="modals-for-auth-cart" tabindex="-1">Modals for Auth / Cart <a class="header-anchor" href="#modals-for-auth-cart" aria-label="Permalink to &quot;Modals for Auth / Cart&quot;">​</a></h2><p>The native app is often configured to open path patterns like <code>/checkout/cart</code> or <code>/customer/login</code> in a <strong>native modal</strong>.</p><ul><li>Ensure your Cart page URL matches the pattern expected by the native app configuration.</li><li>Do not use complex hash routers (<code>/#/cart</code>) as they are harder for native interceptors to detect. Use clean paths.</li></ul><h2 id="history-sync" tabindex="-1">History Sync <a class="header-anchor" href="#history-sync" aria-label="Permalink to &quot;History Sync&quot;">​</a></h2><p>Always include the <code>&lt;HotwireHistorySync /&gt;</code> component. It tells the Native App exactly what the current URL is after a client-side navigation, ensuring the native &quot;Back&quot; button works correctly.</p><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li>Understand <a href="./ssr-vs-client-side-considerations">SSR vs Client-side Considerations</a></li><li>Learn about <a href="./registering-bridge-components">Registering Bridge Components</a></li><li>Explore <a href="./../../core-module/core-overview">Core Module</a></li></ul>',11)])])}const f=e(i,[["render",o]]);export{g as __pageData,f as default};
