import{j as a,c as i,b as t,ah as r}from"./chunks/framework.Br27zpCu.js";const p=JSON.parse('{"title":"Headless First","description":"","frontmatter":{},"headers":[],"relativePath":"bagisto-native/introduction/core-philosophy/headless-first.md","filePath":"bagisto-native/introduction/core-philosophy/headless-first.md","lastUpdated":1769095842000}'),l={name:"bagisto-native/introduction/core-philosophy/headless-first.md"};function s(o,e,n,c,h,d){return t(),i("div",null,[...e[0]||(e[0]=[r('<h1 id="headless-first" tabindex="-1">Headless First <a class="header-anchor" href="#headless-first" aria-label="Permalink to &quot;Headless First&quot;">​</a></h1><p>Bagisto Native embraces a <strong>headless-first architecture</strong>, separating the presentation layer from backend logic.</p><h2 id="what-is-headless-first" tabindex="-1">What is Headless First? <a class="header-anchor" href="#what-is-headless-first" aria-label="Permalink to &quot;What is Headless First?&quot;">​</a></h2><p>Headless First means building systems where the frontend (head) is decoupled from the backend, communicating through APIs.</p><h2 id="key-principles" tabindex="-1">Key Principles <a class="header-anchor" href="#key-principles" aria-label="Permalink to &quot;Key Principles&quot;">​</a></h2><h3 id="api-driven-architecture" tabindex="-1">API-Driven Architecture <a class="header-anchor" href="#api-driven-architecture" aria-label="Permalink to &quot;API-Driven Architecture&quot;">​</a></h3><ul><li>All functionality exposed through APIs</li><li>Clean separation of concerns</li><li>Backend independence</li></ul><h3 id="frontend-flexibility" tabindex="-1">Frontend Flexibility <a class="header-anchor" href="#frontend-flexibility" aria-label="Permalink to &quot;Frontend Flexibility&quot;">​</a></h3><ul><li>Choose any JavaScript framework</li><li>Swap frontends without backend changes</li><li>Multiple frontends from one backend</li></ul><h3 id="backend-agnostic" tabindex="-1">Backend Agnostic <a class="header-anchor" href="#backend-agnostic" aria-label="Permalink to &quot;Backend Agnostic&quot;">​</a></h3><ul><li>Not tied to specific backend platforms</li><li>Use any API-driven system</li><li>Integrate with existing services</li></ul><h2 id="benefits" tabindex="-1">Benefits <a class="header-anchor" href="#benefits" aria-label="Permalink to &quot;Benefits&quot;">​</a></h2><h3 id="freedom-of-choice" tabindex="-1">Freedom of Choice <a class="header-anchor" href="#freedom-of-choice" aria-label="Permalink to &quot;Freedom of Choice&quot;">​</a></h3><ul><li>Select the best backend for your needs</li><li>Use existing backend investments</li><li>Avoid vendor lock-in</li></ul><h3 id="scalability" tabindex="-1">Scalability <a class="header-anchor" href="#scalability" aria-label="Permalink to &quot;Scalability&quot;">​</a></h3><ul><li>Scale frontend and backend independently</li><li>Optimize each layer separately</li><li>Add new channels easily</li></ul><h3 id="future-proof" tabindex="-1">Future-Proof <a class="header-anchor" href="#future-proof" aria-label="Permalink to &quot;Future-Proof&quot;">​</a></h3><ul><li>Adapt to new technologies</li><li>Migrate systems gradually</li><li>Keep options open</li></ul><h2 id="in-practice" tabindex="-1">In Practice <a class="header-anchor" href="#in-practice" aria-label="Permalink to &quot;In Practice&quot;">​</a></h2><p>Bagisto Native works with:</p><ul><li><strong>Bagisto Headless</strong> - Pre-built e-commerce APIs</li><li><strong>Custom APIs</strong> - Your own backend services</li><li><strong>Third-party platforms</strong> - Shopify, WooCommerce, etc.</li><li><strong>Multiple backends</strong> - Combine different services</li></ul><p>The headless approach ensures your native app remains flexible and adaptable as your business grows.</p><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li>Learn about the <a href="./web-native-bridge">Web → Native bridge</a></li><li>Understand the <a href="./../../concepts-architecture/high-level-architecture">High-level Architecture</a></li><li>Explore <a href="./../../concepts-architecture/roles-of-components">Roles of Components</a></li></ul>',24)])])}const f=a(l,[["render",s]]);export{p as __pageData,f as default};
