import{j as i,c as a,b as t,ah as o}from"./chunks/framework.Br27zpCu.js";const u=JSON.parse('{"title":"Web to Native Bridge","description":"","frontmatter":{},"headers":[],"relativePath":"bagisto-native/introduction/core-philosophy/web-native-bridge.md","filePath":"bagisto-native/introduction/core-philosophy/web-native-bridge.md","lastUpdated":1769687094000}'),n={name:"bagisto-native/introduction/core-philosophy/web-native-bridge.md"};function l(r,e,s,c,h,d){return t(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="web-to-native-bridge" tabindex="-1">Web to Native Bridge <a class="header-anchor" href="#web-to-native-bridge" aria-label="Permalink to &quot;Web to Native Bridge&quot;">​</a></h1><p>Bagisto Native creates a <strong>seamless bridge</strong> between your web application and native mobile functionality.</p><h2 id="what-is-the-web-→-native-bridge" tabindex="-1">What is the Web → Native Bridge? <a class="header-anchor" href="#what-is-the-web-→-native-bridge" aria-label="Permalink to &quot;What is the Web → Native Bridge?&quot;">​</a></h2><p>The bridge connects your existing web codebase with native device features, enabling you to build mobile apps without starting from scratch.</p><h2 id="how-it-works" tabindex="-1">How It Works <a class="header-anchor" href="#how-it-works" aria-label="Permalink to &quot;How It Works&quot;">​</a></h2><h3 id="hotwire-technology" tabindex="-1">Hotwire Technology <a class="header-anchor" href="#hotwire-technology" aria-label="Permalink to &quot;Hotwire Technology&quot;">​</a></h3><ul><li><strong>Hotwire Bridge</strong> enables web ↔ native communication</li><li>Web Components trigger native functionality</li><li>Native events inform the web layer</li></ul><h3 id="two-way-communication" tabindex="-1">Two-Way Communication <a class="header-anchor" href="#two-way-communication" aria-label="Permalink to &quot;Two-Way Communication&quot;">​</a></h3><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>Web Application ←→ Hotwire Bridge ←→ Native Mobile App</span></span></code></pre></div><ul><li>Web triggers native features (camera, location)</li><li>Native sends data back to web (search results, coordinates)</li><li>Seamless synchronization between layers</li></ul><h2 id="key-benefits" tabindex="-1">Key Benefits <a class="header-anchor" href="#key-benefits" aria-label="Permalink to &quot;Key Benefits&quot;">​</a></h2><h3 id="reuse-your-web-code" tabindex="-1">Reuse Your Web Code <a class="header-anchor" href="#reuse-your-web-code" aria-label="Permalink to &quot;Reuse Your Web Code&quot;">​</a></h3><ul><li>Leverage existing web application</li><li>No need to rebuild from scratch</li><li>Single codebase for multiple platforms</li></ul><h3 id="native-device-access" tabindex="-1">Native Device Access <a class="header-anchor" href="#native-device-access" aria-label="Permalink to &quot;Native Device Access&quot;">​</a></h3><ul><li>Camera for barcode/image scanning</li><li>GPS for location services</li><li>Native notifications and UI</li></ul><h3 id="unified-development" tabindex="-1">Unified Development <a class="header-anchor" href="#unified-development" aria-label="Permalink to &quot;Unified Development&quot;">​</a></h3><ul><li>Maintain one source of truth</li><li>Update once, deploy everywhere</li><li>Consistent business logic</li></ul><h2 id="components-of-the-bridge" tabindex="-1">Components of the Bridge <a class="header-anchor" href="#components-of-the-bridge" aria-label="Permalink to &quot;Components of the Bridge&quot;">​</a></h2><h3 id="web-components" tabindex="-1">Web Components <a class="header-anchor" href="#web-components" aria-label="Permalink to &quot;Web Components&quot;">​</a></h3><ul><li>Trigger native functionality from web</li><li>Listen for native events</li><li>Synchronize state</li></ul><h3 id="hotwire-bundle" tabindex="-1">Hotwire Bundle <a class="header-anchor" href="#hotwire-bundle" aria-label="Permalink to &quot;Hotwire Bundle&quot;">​</a></h3><ul><li>Core bridge implementation</li><li>Handles communication protocol</li><li>Manages event flow</li></ul><h3 id="native-integration" tabindex="-1">Native Integration <a class="header-anchor" href="#native-integration" aria-label="Permalink to &quot;Native Integration&quot;">​</a></h3><ul><li>Platform-specific implementations</li><li>Device feature access</li><li>Native UI components</li></ul><h2 id="example-flow" tabindex="-1">Example Flow <a class="header-anchor" href="#example-flow" aria-label="Permalink to &quot;Example Flow&quot;">​</a></h2><ol><li>User clicks search on web interface</li><li>Web component sends event through bridge</li><li>Native search component opens</li><li>User enters query using device keyboard</li><li>Native sends result back through bridge</li><li>Web receives and processes search query</li></ol><p>The Web → Native Bridge makes it possible to combine the best of web development efficiency with native mobile capabilities.</p><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li>Understand the <a href="./../../concepts-architecture/high-level-architecture">High-level Architecture</a></li><li>Explore <a href="./../../concepts-architecture/web-native-communication-flow">Web ↔ Native Communication Flow</a></li><li>Learn about <a href="./../../concepts-architecture/roles-of-components">Roles of Components</a></li></ul>',29)])])}const p=i(n,[["render",l]]);export{u as __pageData,p as default};
