import{j as t,c as a,b as o,ah as r}from"./chunks/framework.Br27zpCu.js";const c=JSON.parse('{"title":"Mobile-Only Apps","description":"","frontmatter":{},"headers":[],"relativePath":"bagisto-native/multi-frontend-strategy/future-support/mobile-only-apps.md","filePath":"bagisto-native/multi-frontend-strategy/future-support/mobile-only-apps.md","lastUpdated":1769095842000}'),s={name:"bagisto-native/multi-frontend-strategy/future-support/mobile-only-apps.md"};function i(n,e,l,p,u,d){return o(),a("div",null,[...e[0]||(e[0]=[r('<h1 id="mobile-only-apps" tabindex="-1">Mobile-Only Apps <a class="header-anchor" href="#mobile-only-apps" aria-label="Permalink to &quot;Mobile-Only Apps&quot;">​</a></h1><h2 id="status-supported-by-architecture" tabindex="-1">Status: Supported (by Architecture) <a class="header-anchor" href="#status-supported-by-architecture" aria-label="Permalink to &quot;Status: Supported (by Architecture)&quot;">​</a></h2><p>You might want to build an app that exists <strong>only</strong> on the App Stores and does not have a public website URL.</p><h3 id="strategy" tabindex="-1">Strategy <a class="header-anchor" href="#strategy" aria-label="Permalink to &quot;Strategy&quot;">​</a></h3><ol><li><strong>Headless Host</strong>: You still need a web server (Next.js/React) to serve the HTML content to the app.</li><li><strong>App-Specific Host</strong>: Deploy your Next.js app to a hidden domain or sub-path (e.g., <code>app.your-store.com</code>).</li><li><strong>Authentication</strong>: Configure the Native App&#39;s base URL to this specific host.</li></ol><h3 id="benefits" tabindex="-1">Benefits <a class="header-anchor" href="#benefits" aria-label="Permalink to &quot;Benefits&quot;">​</a></h3><ul><li><strong>Exclusive UI</strong>: You can design the UI purely for mobile gestures without worrying about desktop responsiveness.</li><li><strong>Native Navigation</strong>: You can rely more heavily on native tab bars and navigation controllers since you don&#39;t need to support browser back buttons in the same way.</li></ul><h3 id="considerations" tabindex="-1">Considerations <a class="header-anchor" href="#considerations" aria-label="Permalink to &quot;Considerations&quot;">​</a></h3><ul><li><strong>Deep Linking</strong>: You need to ensure deep links (e.g., from emails) redirect correctly to your app-specific host or open the app directly.</li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li>Explore <a href="./vue">Future Support for Vue</a></li><li>Learn about <a href="./../shared-api-strategy">Shared API Strategy</a></li><li>Understand <a href="./../../getting-started/setup-flow-overview">Getting Started</a></li></ul>',11)])])}const b=t(s,[["render",i]]);export{c as __pageData,b as default};
