import{j as t,c as o,b as i,ah as a}from"./chunks/framework.Br27zpCu.js";const g=JSON.parse('{"title":"How to Setup Apple Account with iOS App and Xcode","description":"","frontmatter":{},"headers":[],"relativePath":"bagisto-native/native-integration/ios/how-to-guides/apple-account-setup.md","filePath":"bagisto-native/native-integration/ios/how-to-guides/apple-account-setup.md","lastUpdated":1769687094000}'),n={name:"bagisto-native/native-integration/ios/how-to-guides/apple-account-setup.md"};function s(r,e,l,p,c,d){return i(),o("div",null,[...e[0]||(e[0]=[a('<h1 id="how-to-setup-apple-account-with-ios-app-and-xcode" tabindex="-1">How to Setup Apple Account with iOS App and Xcode <a class="header-anchor" href="#how-to-setup-apple-account-with-ios-app-and-xcode" aria-label="Permalink to &quot;How to Setup Apple Account with iOS App and Xcode&quot;">​</a></h1><p>To distribute your app or run it on a physical device, you need to link your Apple ID to Xcode.</p><h2 id="step-1-add-apple-id-to-xcode" tabindex="-1">Step 1: Add Apple ID to Xcode <a class="header-anchor" href="#step-1-add-apple-id-to-xcode" aria-label="Permalink to &quot;Step 1: Add Apple ID to Xcode&quot;">​</a></h2><ol><li>Open <strong>Xcode &gt; Settings...</strong> (or Preferences).</li><li>Click the <strong>Accounts</strong> tab.</li><li>Click the <strong>+</strong> button and select <strong>Apple ID</strong>.</li><li>Sign in with your Apple developer credentials.</li></ol><h2 id="step-2-configure-team-settings" tabindex="-1">Step 2: Configure Team Settings <a class="header-anchor" href="#step-2-configure-team-settings" aria-label="Permalink to &quot;Step 2: Configure Team Settings&quot;">​</a></h2><ol><li>Select your project in the Navigator.</li><li>Select your main App Target.</li><li>Go to <strong>Signing &amp; Capabilities</strong>.</li><li>In the <strong>Team</strong> dropdown, select the account you just added.</li></ol><h2 id="step-3-automated-signing-recommended" tabindex="-1">Step 3: Automated Signing (Recommended) <a class="header-anchor" href="#step-3-automated-signing-recommended" aria-label="Permalink to &quot;Step 3: Automated Signing (Recommended)&quot;">​</a></h2><ol><li>Ensure <strong>&quot;Automatically manage signing&quot;</strong> is checked.</li><li>Xcode will automatically create provisioning profiles and certificates for you.</li></ol><h2 id="step-4-register-devices" tabindex="-1">Step 4: Register Devices <a class="header-anchor" href="#step-4-register-devices" aria-label="Permalink to &quot;Step 4: Register Devices&quot;">​</a></h2><p>If you connect a new iPhone, Xcode will ask if you want to register it.</p><ol><li>Click <strong>Register Device</strong>.</li><li>This allows you to install and debug your app on that specific hardware.</li></ol>',11)])])}const h=t(n,[["render",s]]);export{g as __pageData,h as default};
