import{j as e,c as a,b as i,ah as o}from"./chunks/framework.Br27zpCu.js";const g=JSON.parse('{"title":"Simulator vs Device","description":"","frontmatter":{},"headers":[],"relativePath":"bagisto-native/native-integration/ios/ios-app-setup/simulator-vs-device.md","filePath":"bagisto-native/native-integration/ios/ios-app-setup/simulator-vs-device.md","lastUpdated":1769687094000}'),s={name:"bagisto-native/native-integration/ios/ios-app-setup/simulator-vs-device.md"};function r(l,t,n,d,c,u){return i(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="simulator-vs-device" tabindex="-1">Simulator vs Device <a class="header-anchor" href="#simulator-vs-device" aria-label="Permalink to &quot;Simulator vs Device&quot;">​</a></h1><p>Testing your Bagisto Native app on both simulators and physical devices is crucial for a smooth user experience.</p><h2 id="ios-simulator" tabindex="-1">iOS Simulator <a class="header-anchor" href="#ios-simulator" aria-label="Permalink to &quot;iOS Simulator&quot;">​</a></h2><p>The simulator is great for rapid UI development and debugging.</p><ul><li><strong>Pros</strong>: Fast build times, easy to test different screen sizes.</li><li><strong>Cons</strong>: Does not simulate camera, sensors, or real-world network conditions.</li><li><strong>Accessing Localhost</strong>: Use <code>http://localhost:3000</code> or <code>http://127.0.0.1:3000</code>.</li></ul><h2 id="physical-ios-device" tabindex="-1">Physical iOS Device <a class="header-anchor" href="#physical-ios-device" aria-label="Permalink to &quot;Physical iOS Device&quot;">​</a></h2><p>A real device is necessary for final verification and native functionality testing.</p><ul><li><strong>Pros</strong>: Real-world performance, accurate touch interactions, camera support.</li><li><strong>Cons</strong>: Requires a Wi-Fi connection and manual IP configuration.</li><li><strong>Accessing Localhost</strong>: Use your Mac&#39;s IP address (e.g., <code>http://192.168.1.50:3000</code>).</li></ul><h3 id="which-one-to-use" tabindex="-1">Which one to use? <a class="header-anchor" href="#which-one-to-use" aria-label="Permalink to &quot;Which one to use?&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;">Scenario</th><th style="text-align:left;">Simulator</th><th style="text-align:left;">Physical Device</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Initial UI Layout</strong></td><td style="text-align:left;">Preferred</td><td style="text-align:left;">Good</td></tr><tr><td style="text-align:left;"><strong>Native Bridge Tests</strong></td><td style="text-align:left;">Good</td><td style="text-align:left;">Preferred</td></tr><tr><td style="text-align:left;"><strong>Performance Benchmarking</strong></td><td style="text-align:left;">No</td><td style="text-align:left;">Preferred</td></tr><tr><td style="text-align:left;"><strong>Animation Smoothness</strong></td><td style="text-align:left;">No</td><td style="text-align:left;">Preferred</td></tr></tbody></table><div class="tip custom-block github-alert"><p class="custom-block-title">TIP</p><p>Always do a final &quot;smoke test&quot; on a physical device before any major release.</p></div>',11)])])}const p=e(s,[["render",r]]);export{g as __pageData,p as default};
