import{j as t,c as a,b as o,ah as r}from"./chunks/framework.Br27zpCu.js";const m=JSON.parse('{"title":"Headless Commerce Knowledge","description":"","frontmatter":{},"headers":[],"relativePath":"bagisto-native/prerequisites/headless-knowledge.md","filePath":"bagisto-native/prerequisites/headless-knowledge.md","lastUpdated":1769095842000}'),n={name:"bagisto-native/prerequisites/headless-knowledge.md"};function s(i,e,l,d,c,h){return o(),a("div",null,[...e[0]||(e[0]=[r('<h1 id="headless-commerce-knowledge" tabindex="-1">Headless Commerce Knowledge <a class="header-anchor" href="#headless-commerce-knowledge" aria-label="Permalink to &quot;Headless Commerce Knowledge&quot;">​</a></h1><p>Bagisto Native is an advanced implementation of headless commerce. To be successful, you should be familiar with the following concepts.</p><h2 id="core-concepts" tabindex="-1">Core Concepts <a class="header-anchor" href="#core-concepts" aria-label="Permalink to &quot;Core Concepts&quot;">​</a></h2><h3 id="_1-headless-architecture" tabindex="-1">1. Headless Architecture <a class="header-anchor" href="#_1-headless-architecture" aria-label="Permalink to &quot;1. Headless Architecture&quot;">​</a></h3><p>Understanding that the <strong>Frontend</strong> (Next.js/React) is completely decoupled from the <strong>Backend</strong> (Laravel/Bagisto). They communicate strictly via APIs.</p><h3 id="_2-next-js-react" tabindex="-1">2. Next.js &amp; React <a class="header-anchor" href="#_2-next-js-react" aria-label="Permalink to &quot;2. Next.js &amp; React&quot;">​</a></h3><ul><li><strong>Server Components vs Client Components</strong>: Knowing when to use <code>&#39;use client&#39;</code> is critical for Bagisto Native, as bridge components (like <code>HotwireToast</code>) function differently in SSR contexts.</li><li><strong>Routing</strong>: Understanding the Next.js App Router.</li></ul><h3 id="_3-hotwire-turbo-optional-but-recommended" tabindex="-1">3. Hotwire / Turbo (Optional but Recommended) <a class="header-anchor" href="#_3-hotwire-turbo-optional-but-recommended" aria-label="Permalink to &quot;3. Hotwire / Turbo (Optional but Recommended)&quot;">​</a></h3><p>While we provide wrappers, understanding the basics of <strong>Turbo Drive</strong>—how it swaps HTML body content without a full page reload—will help you debug navigation issues between the web and native layers.</p><h2 id="recommended-reading" tabindex="-1">Recommended Reading <a class="header-anchor" href="#recommended-reading" aria-label="Permalink to &quot;Recommended Reading&quot;">​</a></h2><ul><li><a href="https://nextjs.org/docs" target="_blank" rel="noreferrer">Next.js Documentation</a></li><li><a href="https://turbo.hotwired.dev/handbook/introduction" target="_blank" rel="noreferrer">Turbo Handbook</a></li></ul><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to &quot;Next Steps&quot;">​</a></h2><ul><li>Explore <a href="./../getting-started/setup-flow-overview">Getting Started</a></li><li>Understand <a href="./../getting-started/create-headless-commerce">Create Bagisto Headless Commerce</a></li><li>Learn about <a href="./../integrating-native-framework/installing-packages">Installing Packages</a></li></ul>',13)])])}const g=t(n,[["render",s]]);export{m as __pageData,g as default};
